$(function() {
  if ($('#sidebar-sticky').length) { // Ganti "#sticky-sidebar" dengan ID tertentu
    var el = $('##sidebar-sticky');
    var stickyTop = $('##sidebar-sticky').offset().top;
    var stickyHeight = $('#sidebar-sticky').height();
    $(window).scroll(function() {
      var limit = $('bottom').offset().top - stickyHeight - 20; // Jarak berhenti di "#footer-wrapper"
      var windowTop = $(window).scrollTop();
      if (stickyTop < windowTop) {
        el.css({
          position: 'fixed',
          top: 20 // Jarak atau margin sticky dari atas
        });
      } else {
        el.css('position', 'static');
      }
      if (limit < windowTop) {
        var diff = limit - windowTop;
        el.css({
          top: diff
        });
      }
    });
  }
});
